INSERT INTO ROKOMARI.AUTHOR (AUTHOR_ID, AUTHOR_NAME, IMAGE, BIOGRAPHY) VALUES (358, 'মুস্তাফা জামান আব্বাসী', 'https://ds.rokomari.store/rokomari110/people/ade96eac2_10.jpg', 'মুস্তাফা জামান আব্বাসী (জন্ম: ৮ ডিসেম্বর, ১৯৩৬) একজন বাংলাদেশি সঙ্গীতজ্ঞ এবং সঙ্গীত বিষয়ক অধ্যাপক ও গবেষক। তিনি লোক সঙ্গীতশিল্পী ও সুরকার আব্বাসউদ্দীন আহমদের কনিষ্ঠপুত্র। সঙ্গীতে অবদানের জন্য তিনি ১৯৯৫ সালে বাংলাদেশ সরকার প্রদত্ত সর্বোচ্চ বেসামরিক পুরস্কার একুশে পদকে ভূষিত হন এবং লেখালেখির স্বীকৃতি স্বরূপ পেয়েছেন বাংলা একাডেমি ফেলোশিপ। তিনি ইন্ডিপেন্ডেন্ট ইউনিভার্সিটির "কাজী নজরুল ইসলাম এবং আব্বাসউদ্দীন আহমদ গবেষণা ও শিক্ষা কেন্দ্র" এর গবেষক। নজরুল ইসলামকে নিয়ে তার কাজের জন্য তিনি ২০১৩ সালে নজরুল মেলায় আজীবন সম্মাননা লাভ করেন। তিনি তৎকালীন ব্রিটিশ ভারতের পশ্চিমবঙ্গের কুচবিহার জেলায় জন্মগ্রহণ করেন। তার বড় ভাই মোস্তফা কামাল ছিলেন বাংলাদেশের প্রধান বিচারপতি। তার বোন ফেরদৌসী রহমান একজন সুরকার ও সঙ্গীতশিল্পী। আব্বাসী ভারতীয় শাস্ত্রীয় সঙ্গীতজ্ঞ ওস্তাদ মুহম্মদ হোসেন খসরু এবং ওস্তাদ গুল মোহাম্মদ খাঁর নিকট সঙ্গীতের তালিম গ্রহণ করেন। তিনি মোট ৫০টি বই রচনা/সম্পাদনা করেন। তিনি ভাওয়াইয়া গানের উপর দুটি বই প্রকাশ করেন যার মধ্যে ১২০০ গানের উল্লেখ রয়েছে। তিনি আমার ঠিকানা এবং ভরা নদীর বাঁকে টেলিভিশন অনুষ্ঠানের উপস্থাপনা করেছেন। অনুষ্ঠান দুটি বাংলাদেশ টেলিভিশনে প্রচারিত হত।');
