INSERT INTO ROKOMARI.AUTHOR (AUTHOR_ID, AUTHOR_NAME, IMAGE, BIOGRAPHY) VALUES (375, 'আল মাহমুদ', 'https://ds.rokomari.store/rokomari110/people/b623143d88a4_63.jpg', 'বাংলা সাহিত্যের শ্রেষ্ঠ কবিদের একজন আল মাহমুদের কবিতার বই পড়েননি এমন সাহিত্যপ্রেমী খুঁজে পাওয়া ভার। গুণী এই কবি একাধারে একজন সাংবাদিক, শিশুসাহিত্যিক, ঔপন্যাসিক ও প্রাবন্ধিক। তবে সবকিছু ছাপিয়ে গেছে তার কবি পরিচয়। আধুনিক বাংলা কবিতা নানা দিক থেকে তার কাছে ঋণী থাকবে। বাচনভঙ্গি আর রচনাশৈলীতে তার কবিতা সমকালীন যেকোনো কবির তুলনায় অনন্য। ‘কবিতাসমগ্র’ (দুই খন্ড) ‘উড়ালকাব্য’, ‘সোনালি কাবিন’, ‘আল মাহমুদের স্বাধীনতার কবিতা’, ‘প্রেমের কবিতা সমগ্র’, ‘আল মাহমুদের শ্রেষ্ঠ কবিতা’ ইত্যাদি কবিতার বই নিয়ে আল মাহমুদ কবিতাসমগ্র। এছাড়াও আল মাহমুদ উপন্যাস সমগ্র প্রকাশিত হয়েছে তিন খণ্ডে');
